import { useState, useEffect } from 'react';
import React from 'react'
import styles from "./Step.module.css"
import { useParams } from "react-router-dom";

const Step = () => {
    const id = useParams();
    const [change, setChange] = useState(0);
    const [stuff, setStuff] = useState(null);
    const [name, setName] = useState("")
    const [image, setImage] = useState("")
    const [description, setDescription] = useState("")
    const [nextStep, setNextStep] = useState([])
    const [prevStep, setPreviousStep] = useState([])
    const [data, setData] = useState({
        image: "",
        nextSteps:[],
        previousSteps:[],
        name:'',
        description:'',
        next: [], 
        prev: []

    });   

    useEffect(() => {
        const showStep = (async () => {

            const url = "http://localhost:5000/step/get_by_id/" + id.id;
            await( await fetch(url, {
                method: 'GET',
            })).json()
            .then(res => {
                // console.log(res)
                setData(res)
            })
        })
        showStep();
    }, []);

    async function handleStepFinder() {
        const temp = {name: stepName};
        console.log(stepName)
        const url = "http://localhost:5000/step/find/";
        await(await fetch(url, {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(temp),
        })).json()
        .then(res => {
            console.log(res)
            setStuff(
            <div className="w3-container w3-display-container w3-padding-16">
                <i onClick="w3_close()" className="fa fa-remove w3-hide-large w3-button w3-transparent w3-display-topright" />
                <h3>Kết quả tìm kiếm</h3>
                <hr />

