import { useState, useEffect } from 'react';
import React from 'react'
import styles from "./Signup.module.css"
import { useParams } from "react-router-dom";

const Signup = () => {

    const [phone, setPhone] = useState('');
    const [password, setPassword] = useState('');
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [address, setAddress] = useState('');
    const [error, setError] = useState('');

    const handleRegister = (e) => {
        if (!phone || !password || !name || !email || !address) {
            setError('Bạn nhập thiếu một số trường thông tin! Nhập lại!');
        } else if (password.length <= 6) {
            setError('Mật khẩu quá yếu, xin hãy nhập mật khẩu lớn hơn 6 ký tự!')
        } else {
            setError('');
            axios({
                method: 'POST',
                url: 'http://localhost:5000/customer/add',
                data: {
                    phone: phone,
                    password: password,
                    name: name,
                    email: email,
                    address: address,
                }
            })
                .then(res => {
                    console.log(res);
                    //e.preventDefault();
                    alert('Đăng ký Thành công, Nhấn oke để về trang chủ');
                    window.location.href = ROUTES.BASE_URL_WEB;
                })
                .catch(err => {
                    // console.log(err);
                    console.log(err.response.data);
                    // e.prevetnDefault();
                    setError(err.response.data);
                })
        }
        // console.error('Đăng ký thành công, quay trở lại trang chủ để đăng nhập');
        // console.log(e)
        //    e.preventDefaut();
    }

    return (
        <div>
        <h2>Modal Login Form</h2>
        <button onclick="document.getElementById('id01').style.display='block'" style={{width: 'auto'}}>Login</button>
        <div id="id01" className="modal">
          <form className="modal-content animate" action="/action_page.php" method="post">
            <div className="imgcontainer">
              <span onclick="document.getElementById('id01').style.display='none'" className="close" title="Close Modal">×</span>
              <img src="img_avatar2.png" alt="Avatar" className="avatar" />
            </div>
            <div className="container">
              <label htmlFor="uname"><b>Username</b></label>
              <input type="text" placeholder="Enter Username" name="uname" required />
              <label htmlFor="psw"><b>Password</b></label>
              <input type="password" placeholder="Enter Password" name="psw" required />
              <button type="submit">Login</button>
              <label>
                <input type="checkbox" defaultChecked="checked" name="remember" /> Remember me
              </label>
            </div>
            <div className="container" style={{backgroundColor: '#f1f1f1'}}>
              <button type="button" onclick="document.getElementById('id01').style.display='none'" className="cancelbtn">Cancel</button>
              <span className="psw">Forgot <a href="#">password?</a></span>
            </div>
          </form>
        </div>
      </div>
    );
}
