import { useState, useEffect } from 'react';
import React from 'react'
import styles from "./Admin.module.css"
import { useParams } from "react-router-dom";

const Admin = () => {
    const id = useParams();
    const [change, setChange] = useState(0);
    const [prevStuff, setPrevStuff] = useState(null);
    const [nextStuff, setNextStuff] = useState(null);
    const [name, setName] = useState("")
    const [stepName, setStepName] = useState("")
    const [image, setImage] = useState("")
    const [description, setDescription] = useState("")
    const [nextStep, setNextStep] = useState([])
    const [previousStep, setPreviousStep] = useState([])
    const [prevChecked, setPrevChecked] = useState("");
    const [nextChecked, setNextChecked] = useState("");
    const [data, setData] = useState({
        image: "",
        nextSteps:[],
        previousSteps:[],
        name:'',
        description:'',
        next: [], 
        prev: []

    });   

    useEffect(() => {
        const showAdmin = (async () => {

            const url = "http://localhost:5000/Admin/get_by_id/" + id.id;
            await( await fetch(url, {
                method: 'GET',
            })).json()
            .then(res => {
                // console.log(res)
                setData(res)
            })
        })
        showAdmin();
    }, []);

    function handleAddToPrevStep() {
        setPreviousStep(previousStep.push(prevChecked))
    }

    function handleAddToNextStep() {
        setNextStep(nextStep.push(nextChecked))
    }

    async function handleAddStep() {

    }

    async function handleDeleteStep() {
        
    }

    async function handleDamnStepFinder() {
        
    }

    async function handlePrevStepFinder() {
        const temp = {name: stepName};
        console.log(stepName)
        const url = "http://localhost:5000/step/find/";
        await(await fetch(url, {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(temp),
        })).json()
        .then(res => {
            console.log(res)
            setPrevStuff(
                res.map(data => {
                    return(
                        <div className="w3-col" style={{margin:'auto', display:'flex', justifyContent: 'space-between',}}>
                            <label style={{marginLeft:'5px'}} for={data.id}>{data.name}</label>
                            <input style={{margin: 'auto 0 auto auto'}} type="radio" id={data.id} name="prevSteps" checked={prevChecked === data.id} onChange={() => setPrevChecked(data.id)}/>
                        </div> 
                           
                    // <ul className="w3-ul">
                    //     <li className="w3-ul li"  className={styles.find_item} style={{padding: '8px 8px'}}>
                    //         <a href= {"http://localhost:3000/" + data.id} className={styles.find_item_name}>{data.name}</a>
                    //         <img src={data.image} className={styles.find_item_img}></img>
                    //     </li>
                    // </ul>
                    )
                })
            )
            setChange(change+1);
        });
    }

    // useEffect(() => {
    //         oke.push(<div className="w3-container w3-display-container w3-padding-16">
    //         asdfasdf
    //     </div>)
    //     console.log(change)
    // }, [change]);

    return (
        <div>
            <div className="w3-main w3-white">
            {/* Push down content on small screens */}
            <div className="w3-hide-large" style={{marginTop: '80px'}} />
            {/* Slideshow Header */}
            <div className="w3-container" id="apartment">
                <h2 className="w3-text-green">Trang Admin</h2>
                {/* <div className="w3-display-container">
                <img src={data.image} style={{width: '100%', marginBottom: '-6px'}} />
                <div className="w3-display-bottomleft w3-container w3-black">
                    <p>Trang Admin</p>
                </div>
                </div>     */}
            </div>
            <div className="w3-container" style={{paddingTop: '16px'}}>
                <h4><strong>Thêm bước</strong></h4>
                <div className="">
                    <label htmlFor="name"><b>Tên bước</b></label>
                    <input className="w3-input w3-border" type="text" placeholder="Nhập tên bước" name="name" style={{margin: '8px 0px'}} required onChange={(e) => setName(e.target.value)}/>
                    <label htmlFor="description"><b>Mô tả</b></label>
                    <input className="w3-input w3-border" type="text" placeholder="Nhập mô tả" name="description" style={{margin: '8px 0px'}} required onChange={(e) => setDescription(e.target.value)}/>
                    <label htmlFor="image"><b>Link ảnh</b></label>
                    <input className="w3-input w3-border" type="text" placeholder="Nhập link ảnh" name="image" style={{margin: '8px 0px'}} required onChange={(e) => setImage(e.target.value)}/>
                
                    <label htmlFor="image"><b>Tìm kiếm các bước liên quan</b></label>
                
                <div className="w3-row w3-large">
                    <ul className="w3-col s6 " style={{paddingLeft: '0PX', borderRight: "1px solid #ccc"}}>
                    <div className="w3-green" style={{textAlign: 'center', listStyleType: "none", padding: '8px 0px'}}>Các bước trước</div>
                        <div style={{display: 'flex', justifyContent: "space-between"}}>
                            <input className="w3-input w3-border" type="text" placeholder="Nhập tên bước" name="description" style={{margin: '20px 0px'}} required onChange={(e) => setDescription(e.target.value)}/>
                            <button className="w3-button w3-block w3-green w3-col s4" style={{margin: '20px 0px', padding: '13px 0px'}} onClick={handlePrevStepFinder}>Tìm kiếm</button>
                        </div>
                        <li className="w3-ul li"  className={styles.find_item} style={{padding: '8px 8px'}}></li>
                        {prevStuff}
                        {
                            data.next.map(data1 => {
                                return(
                                    <li className="w3-ul li"  className={styles.find_item} style={{padding: '8px 8px'}}>
                                    <a href= {"http://localhost:3000/" + data1.id} className={styles.find_item_name}>{data1.name}</a>
                                    </li>
                                )
                            }) 
                        }
                        <button className="w3-button w3-block w3-green" style={{margin: '20px 0px', padding: '13px 0px'}} onClick={handleAddToPrevStep}>Thêm vào danh sách trước</button>
                    </ul>
                    <ul className="w3-col s6 " style={{paddingLeft: '0PX', borderRight: "1px solid #ccc"}}>
                    <div className="w3-green" style={{textAlign: 'center', listStyleType: "none", padding: '8px 0px'}}>Các bước sau</div>
                        <div style={{display: 'flex', justifyContent: "space-between"}}>
                            <input className="w3-input w3-border" type="text" placeholder="Nhập tên bước" name="description" style={{margin: '20px 0px'}} required onChange={(e) => setDescription(e.target.value)}/>
                            <button className="w3-button w3-block w3-green w3-col s4" style={{margin: '20px 0px', padding: '13px 0px'}} onClick={handlePrevStepFinder}>Tìm kiếm</button>
                        </div>
                        <li className="w3-ul li"  className={styles.find_item} style={{padding: '8px 8px'}}></li>
                        {nextStuff}
                        {
                            data.prev.map(data2 => {
                                return(
                                    <li className="w3-ul li"  className={styles.find_item} style={{padding: '8px 8px'}}>
                                            <a href= {"http://localhost:3000/" + data2.id} className={styles.find_item_name}>{data2.name}</a>
                                    </li>
                                )
                            }) 
                        }
                        <button className="w3-button w3-block w3-green" style={{margin: '20px 0px', padding: '13px 0px'}} onClick={handleAddToNextStep}>Thêm vào danh sách sau</button>
                    </ul>
                </div>
                </div>
                <hr />
                    <button className="w3-button w3-block w3-green" style={{margin: '20px 0px', padding: '13px 0px'}} onClick={handleAddStep}>Thêm bước</button>
                <hr />
                <h4><strong>Xóa bước</strong></h4>
                <ul className="w3-col " style={{paddingLeft: '0PX', borderRight: "1px solid #ccc"}}>
                            <div style={{display: 'flex', justifyContent: "space-between"}}>
                                <input className="w3-input w3-border" type="text" placeholder="Nhập tên bước" name="description" style={{margin: '20px 0px'}} required onChange={(e) => setDescription(e.target.value)}/>
                                <button className="w3-button w3-block w3-green w3-col s4" style={{margin: '20px 0px', padding: '13px 0px'}} onClick={handleDamnStepFinder}>Tìm kiếm</button>
                            </div>
                            {
                                data.next.map(data1 => {
                                    return(
                                        <li className="w3-ul li"  className={styles.find_item} style={{padding: '8px 8px'}}>
                                        <a href= {"http://localhost:3000/" + data1.id} className={styles.find_item_name}>{data1.name}</a>
                                        </li>
                                    )
                                }) 
                            }
                        </ul>
                        <button className="w3-button w3-block w3-green" style={{margin: '20px 0px', padding: '13px 0px'}} onClick={handleDeleteStep}>Xóa bước</button>

                
            </div>
            
            </div>
        </div>
    );

}
export default Admin
