import {useState} from 'react'
import {Link, useNavigate} from 'react-router-dom'
import axios from 'axios'
import * as isLoggedIn from '../constants/isLoggedIn'
import {deleteCookie, setCookie} from '../constants/userCookie'


const eye = "far fa-eye";
const eye_slash = "far fa-eye-slash"
const users = [
    {
        id: 1,
        name: 'Người dùng'
    },
    {
        id: 2,
        name: 'Admin hệ thống'
    }
]


const Login = () => {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [checked, setChecked] = useState(1);
    const [errorMsg, setErrorMsg] = useState("");
    const [isShowPassword, setIsShowPassword] = useState(false);

    const navigate = useNavigate();
    const value = {
        'email': email,
        'password': password,
        'role': checked,
    }
    // console.log(isShowPassword);

    const handleShowHidePassword = (e) => {
        setIsShowPassword(!isShowPassword);
        // console.log('Show hide password')
    }
    // console.log(checked);
    // console.log(value);
    // let path = (value.role === 1) ? ROUTES.HOME : ((value.role === 2) ? ROUTES.HOMESTAFF : ROUTES.HOMEADMIN);
    //     console.log(path);
    // let data = {
    //     name: 'Chu Mạnh Tiến',
    //     ID: 20194182
    // }
    // let datanew = JSON.stringify(data);
    // console.log(datanew);
    // setCookie('user', datanew, 1);

    // deleteCookie('user', datanew, 1);
    const handleLogin = (e) => {
        // alert('Bạn đã đăng nhập.');
        // console.log(value);
        // console.log("Tài khoản đăng nhập:")
        // console.log('email: ', value.email);
        // console.log('Password: ', value.password);
        // setErrorMsg('');
        axios({
            method: 'POST',
            url: 'http://localhost:5000/user/login',
            data: {
                email: email,
                password: password,
                role: checked
            }
        })
            .then(res => {
                console.log(res.data);
                console.log(typeof (res.data));

                let username = (value.role === 1) ? 'user' : 'admin';
                let end_path = (value.role === 1) ? 'ROUTES.HOME': 'ROUTES.HOMEADMIN'
                window.location.href = ('ROUTES.BASE_URL_WEB' + end_path);
                setCookie(username, JSON.stringify(res.data), 1);
                setCookie(isLoggedIn[username], 'true', 1);
            })
            .catch(error => {
                console.log(error.response.data);
                error = Object.values(error.response.data);
                // console.log("sai mật khẩu")
                setErrorMsg(error);
            })
        //Test
        // let path = (value.role === 1) ? ROUTES.HOME : ((value.role === 2) ? ROUTES.HOMESTAFF : ROUTES.HOMEADMIN);
        // let username = (value.role === 1) ? 'user' : ((value.role === 2) ? 'staff' : 'admin');

        // let data = {
        //     name: 'Chu Mạnh Tiến',
        //     ID: `${username.toUpperCase()}000${value.role}`
        // }
        // let datanew = JSON.stringify(data);
        // console.log(datanew);
        // setCookie(username, datanew, 1);
        // setCookie(isLogined[username], 'true', 1);
        // window.location.href = `${ROUTES.BASE_URL_WEB + path}`
        // e.preventDefault();
    }


    return (
        <div className="w3-container" style={{margin: '10px 32px 32px 32px'}}>
        <h2 ><strong>Tra cứu thông tin nghiệp vụ</strong></h2>
        <div id="id01" className="modal" style={{marginTop: '24px'}}>
            <form className="modal-content animate" action="/action_page.php" method="post">
                <div className="">
                    <img src="https://pbs.twimg.com/media/EmMIQF9VcAEfSzc.jpg" style={{width: '100%', height: '400px', objectFit: "cover",  marginBottom: '16px'}} alt="Avatar" className="avatar" />
                </div>
                <div className="">
                    <label htmlFor="uname"><b>Họ tên</b></label>
                    <input className="w3-input w3-border" type="text" placeholder="Nhập họ tên" name="name" required onChange={(e) => setName(e.target.value)}/>
                    <label htmlFor="uname"><b>Email</b></label>
                    <input className="w3-input w3-border" type="text" placeholder="Nhập email" name="email" required onChange={(e) => setEmail(e.target.value)}/>
                    <label htmlFor="psw"><b>Mật khẩu</b></label>
                    <input className="w3-input w3-border" type="password" placeholder="Nhập mật khẩu" name="password" required onChange={(e) => setPassword(e.target.value)}/>
                    <button className="w3-button w3-block w3-green" style={{margin: '15px 0px', padding: '13px 0px'}} onClick={handleLogin}>Đăng ký</button>
                </div>
                <div className="w3-container w3-block" style={{ display: 'flex', justifyContent: "space-between", padding: '6px 0px'}}>
                    <label className="w3-col s6">
                        {/* <input type="checkbox" defaultChecked="checked" name="remember" /> Remember me */}
                    </label>
                    <div className="w3-col s6" style={{textAlign: 'Right'}}>
                            Đã có tài khoản? {' '}
                            <Link className="w3-text-green" style={{paddingRight:'0px'}} to={''}>Đăng nhập</Link>
                    </div>
                </div>
            </form>
        </div>
        </div>
    );
}

export default Login;
