import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import {useState} from 'react';
import {useEffect} from 'react';
import React from 'react'
import styles from "./Step.module.css"
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useParams
  } from "react-router-dom";

const axios = require('axios');
const schema = yup.object().shape({
    name: yup
        .string()
        .required("Vui lòng nhập tên bước!")
        .max(50, "Tên tối đa 50 ký tự!"),
    image: yup
        .string(),
    description: yup
        .string()
        .required("Vui lòng nhập tên bước!")

});








const Step = () => {
    const id = useParams();
    const [change, setChange] = useState(0);
    const [stuff, setStuff] = useState(null);
    const [data, setData] = useState({image: "https://i.pinimg.com/736x/c1/ab/68/c1ab688b3d50163a1e0f3a44f7e18767.jpg"});
    // const {
    //     register,
    //     reset,
    //     setFocus,
    //     handleSubmit,
    //     formState: { errors }
    // } = useForm({ resolver: yupResolver(schema) });
let arr
let af = 0
let oke = []


    useEffect(() => {
        const showStep = (async () => {

            const url = "http://localhost:5000/step/get_by_id/" + id.id;
            await( await fetch(url, {
                method: 'GET',
            })).json()
            .then(res => {
                setData(res)
            })
            .then(() => {
                step = data.nextSteps.forEach((step) => {
                    return step;
                })
            })
        })
        showStep();
    }, []);

    const handleStepFinder = (async (name) => {
        const temp = {name: name};
        const url = "http://localhost:5000/step/find/";
        await(await fetch(url, {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(temp),
        })).json()
        .then(res => {
            console.log(typeof(res))
            setStuff(
            <div className="w3-container w3-display-container w3-padding-16">
                <i onClick="w3_close()" className="fa fa-remove w3-hide-large w3-button w3-transparent w3-display-topright" />
                <h3>Tìm kiếm bước</h3>
                <hr />

                {res.map(data => {
                    return(
                    <ul className="w3-ul">
                        <li className="w3-ul li"  className={styles.find_item} style={{padding: '8px 8px'}}>
                                <a href= {"http://localhost:3000/" + data.id} className={styles.find_item_name}>{data.name}</a>
                                <img src={data.image} className={styles.find_item_img}></img>
                        </li>
                    </ul>
                    )
                })}


            </div>
            )
            setChange(change+1);
            console.log(change)
        });
    })

    // useEffect(() => {
    //         oke.push(<div className="w3-container w3-display-container w3-padding-16">
    //         asdfasdf
    //     </div>)
    //     console.log(change)
    // }, [change]);

    return (
        <div>
            {/* Sidebar/menu */}
            <nav className="w3-sidebar w3-light-grey w3-collapse w3-top" style={{zIndex: 3, width: '260px'}} id="mySidebar">
            <div className="w3-container w3-display-container w3-padding-16">
                <i onClick="w3_close()" className="fa fa-remove w3-hide-large w3-button w3-transparent w3-display-topright" />
                <h3>Tìm kiếm bước</h3>
                <hr />
                <form action="/action_page.php" target="_blank">
                <p><label><i className="fa fa-calendar-check-o" /> Tên bước</label></p>
                <input className="w3-input w3-border" type="text" placeholder="VD: 'Thanh toán'" name="CheckIn" required />          
                <p><label><i className="fa fa-calendar-o" /> Bước trước</label></p>
                <input className="w3-input w3-border" type="text" placeholder="VD: 'Thanh toán'" name="CheckOut" required />         
                <p><label><i className="fa fa-calendar-o" /> Bước sau</label></p>
                <input className="w3-input w3-border" type="text" placeholder="VD: 'Thanh toán'" name="CheckOut" required />         
                <p><button className="w3-button w3-block w3-green w3-left-align" type="submit"><i className="fa fa-search w3-margin-right" /> Tìm kiếm</button></p>
                </form>
            </div>
            {stuff}
            </nav>
            {/* Top menu on small screens */}
            <header className="w3-bar w3-top w3-hide-large w3-black w3-xlarge">
            <span className="w3-bar-item">Các bước</span>
            <a href="javascript:void(0)" className="w3-right w3-bar-item w3-button" onClick="w3_open()"><i className="fa fa-bars" /></a>
            </header>
            {/* Overlay effect when opening sidebar on small screens */}
            <div className="w3-overlay w3-hide-large" onClick="w3_close()" style={{cursor: 'pointer'}} title="close side menu" id="myOverlay" />
            {/* !PAGE CONTENT! */}
            <div className="w3-main w3-white" style={{marginLeft: '260px'}}>
            {/* Push down content on small screens */}
            <div className="w3-hide-large" style={{marginTop: '80px'}} />
            {/* Slideshow Header */}
            <div className="w3-container" id="apartment">
                <h2 className="w3-text-green">The Apartment</h2>
                <div className="w3-display-container">
                <img src={data.image} style={{width: '100%', marginBottom: '-6px'}} />
                <div className="w3-display-bottomleft w3-container w3-black">
                    <p>{data.name}</p>
                </div>
                </div>    
            </div>
            <div className="w3-container" style={{paddingTop: '16px'}}>
                <h4><strong>Thông tin chi tiết</strong></h4>
                <p>{data.description}</p>
                <hr />
                <h4><strong>Amenities</strong></h4>
                <div className="w3-row w3-large">
                    {}
                    {/* {
                       data.nextSteps.map(data1 => {
                           console.log(data1)
                        return(
                        <ul className="w3-ul">
                            <li className="w3-ul li"  className={styles.find_item} style={{padding: '8px 8px'}}>
                                    <a href= {"http://localhost:3000/"} className={styles.find_item_name}>asd</a>
                                    <img src='' className={styles.find_item_img}></img>
                            </li>
                        </ul>
                        )
                    }) 
                    } */}
                    {/* {
                       data.nextSteps.map(data => {
                        return(
                        <ul className="w3-ul">
                            <li className="w3-ul li"  className={styles.find_item} style={{padding: '8px 8px'}}>
                                    <a href= {"http://localhost:3000/" + data.id} className={styles.find_item_name}>{data.name}</a>
                                    <img src={data.image} className={styles.find_item_img}></img>
                            </li>
                        </ul>
                        )
                    }) 
