import { useState, useEffect } from 'react';
import React from 'react'
import {Link} from 'react-router-dom';
import styles from "./Signup.module.css"
import { useParams } from "react-router-dom";
import axios from 'axios';
const Signup = () => {

    const [phone, setPhone] = useState('');
    const [password, setPassword] = useState('');
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [address, setAddress] = useState('');
    const [error, setError] = useState('');

    const handleRegister = (e) => {
        if (!password || !name || !email) {
            setError('Bạn nhập thiếu một số trường thông tin! Nhập lại!');
        } else if (password.length <= 6) {
            setError('Mật khẩu quá yếu, xin hãy nhập mật khẩu lớn hơn 6 ký tự!')
        } else {
            setError('');
            axios({
                method: 'POST',
                url: 'http://localhost:5000/user/signup',
                data: {
                    password: password,
                    name: name,
                    email: email,
                }
            })
                .then(res => {
                    console.log(res);
                    //e.preventDefault();
                    alert('Đăng ký Thành công, Nhấn oke để về trang chủ');
                    window.location.href = '';
                })
                .catch(err => {
                    // console.log(err);
                    console.log(err.response.data);
                    // e.prevetnDefault();
                    setError(err.response.data);
                })
        }
    }

    return (
        <div className="w3-container" style={{margin: '24px'}}>
        <h2 ><strong>Tra cứu thông tin quy trình nghiệp vụ</strong></h2>
        <div id="id01" className="modal">
            <form className="modal-content animate" action="/action_page.php" method="post">
                <div className="">
                    <span onclick="document.getElementById('id01').style.display='none'" className="close" title="Close Modal">×</span>
                <img src="https://pbs.twimg.com/media/EmMIQF9VcAEfSzc.jpg" style={{width: '100%', marginBottom: '16px'}} alt="Avatar" className="avatar" />
                </div>
                <div className="">
                    <label htmlFor="uname"><b>Họ tên</b></label>
                    <input className="w3-input w3-border" type="text" placeholder="Nhập họ tên" name="name" required onChange={(e) => setName(e.target.value)}/>
                    <label htmlFor="uname"><b>Email</b></label>
                    <input className="w3-input w3-border" type="text" placeholder="Nhập email" name="email" required onChange={(e) => setEmail(e.target.value)}/>
                    <label htmlFor="psw"><b>Mật khẩu</b></label>
                    <input className="w3-input w3-border" type="password" placeholder="Nhập mật khẩu" name="password" required onChange={(e) => setPassword(e.target.value)}/>
                    <button className="w3-button w3-block w3-green" style={{margin: '15px 0px', padding: '13px 0px'}} onClick={handleRegister}>Đăng ký</button>
                </div>
                <div className="w3-container w3-block" style={{ display: 'flex', justifyContent: "space-between", padding: '6px 0px'}}>
                    <label className="w3-col s6">
                        {/* <input type="checkbox" defaultChecked="checked" name="remember" /> Remember me */}
                    </label>
                    <div className="w3-col s6" style={{textAlign: 'Right'}}>
                            Đã có tài khoản? {' '}
                            <Link className="w3-text-green" style={{paddingRight:'0px'}} to={''}>Đăng nhập</Link>
                    </div>
                </div>
            </form>
        </div>
        </div>
    );
}

export default Signup;
